const lcd = new five.LCD({controller: "PCF8574A"});
const moment = global.get('moment');
moment.locale('ru');

node.on('input', (msg) => {
    const now = moment(msg.payload);
    const date = now.format('ll');
    const time = now.format('LTS');
    lcd.cursor(0, 0).print(date);
    lcd.cursor(1, 0).print(time);
});
